import React from 'react';
import Button from 'react-bootstrap/Button';

import Stock from './Stock';

import './portfolio.css';


export default class portfolio extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            stockAmount: 5,
            stocks: [
                {
                    symbol: "FB",
                    price: 10,
                    volume: 100,
                    quantity: 500,
                    purchased_value: 5000,
                    date: new Date()
                },
                {
                    symbol: "AMZN",
                    price: 100,
                    volume: 10,
                    quantity: 200,
                    purchased_value: 8000,
                    date: new Date()
                },
                {
                    symbol: "AAPL",
                    price: 50,
                    volume: 10,
                    quantity: 72,
                    purchased_value: 10000,
                    date: new Date()
                }
            ]
        };
    }

    render(){
        let total_val = 0;
        this.state.stocks.forEach((stock, total_val) => total_val += stock.purchased_value);
        return (
            <div className="portfolio__">
                <h1>Total Value of Assets: {}</h1>

                {this.state.stocks.map(
                    stock => {
                        return <Stock 
                                    symbol={stock.symbol}
                                    price={stock.price}
                                    volume={stock.volume}
                                    quantity={stock.quantity}
                                    purchased = {stock.purchased_value}
                                    // date={stock.date}
                                />
                    }
                )}
                <Button variant="secondary" size="lg" block>
                    Block level button
                </Button>
            </div>
        );
    }
}
