import React from 'react';
import Button from 'react-bootstrap/Button';
import { Redirect } from 'react-router-dom';

import Stock from './Stock';

import './portfolio.css';


export default class portfolio extends React.Component{
    constructor(props){
        super(props);
        this.addStock = this.addStock.bind(this);
        this.state = {
            error: false,
            stockAmount: 0,
            stocks: []
        };
    }

    componentDidMount(){
        fetch('/portfolios', {
            method: 'GET',
            headers: {'Content-Type': 'application/json'},
        })
        .then(response => response.json())
        .then(data=>{
            if (data.errorMessage){
                this.setState({ error: true });
            }
            else{
                this.setState({ stocks: data.stocks });
            }
            });
    }

    addStock(){
        const temp = this.state.stocks;
        temp.push({
            symbol: "AAPL",
            price: 50,
            volume: 10,
            quantity: 72,
            purchased_value: 10000,
            date: new Date()
        });
        this.setState({ stocks: temp })
    }

    render(){
        if (this.state.error){
            return <Redirect to="/SignIn"/>
        }
        let total_val = 0;
        for (let i = 0; i < this.state.stocks.length; i++){
            total_val += this.state.stocks[i].purchased_value;
        }

        return (
            <div className="portfolio__">
                <h1>Total Value of Assets: $ {total_val}.00</h1>

                {this.state.stocks.map(
                    stock => {
                        return <Stock 
                                    symbol={stock.symbol}
                                    price={stock.price}
                                    volume={stock.volume}
                                    quantity={stock.shares}
                                    purchased = {stock.purchased_value}
                                    // date={stock.date}
                                />
                    }
                )}
                <Button variant="secondary" size="lg" block onClick={this.addStock}>
                    Block level button
                </Button>
            </div>
        );
    }
}
